sys_script_include.script:
var GetDepartment = Class.create();
GetDepartment.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getDepartment: function() {

		var departmentValue = new GlideRecord('sys_user');
		departmentValue.get(this.getParameter('sysparm_fireUP'));
		return departmentValue.last_name + '' ;

	},

	type: 'GetDepartment'
});
